parameters.url:
={{
  // The expression checks if $json.data[0].data[0].ogImage exists.
  // It then attempts to replace '<SCR>' with the encoded download URL.
  // If $json.data[0].content.download_url does not exist or is empty,
  // '<SCR>' is replaced with an empty string.
  ($item("0").$node["Final Meta"].json["data"]["0"]["ogImage"]).replace('<SCR>', 
    $ifEmpty(
       $json["download_url"], 
      (
        // If the image URL or its components don't exist, construct a default image URL based on other JSON values
        // Get the "Dark Mode" setting from the API Auth node
        "https://cdn.statically.io/og/theme=" +
          ($item("0").$node["API Auth"].json["Dark Mode ?"] + "")
            .replace("false", "white/")
            .replace("true", "dark/")+
          ".jpg"
      )
    ).urlEncode()
  )
  }}

